mapper.update:
UPDATE STORE
SET STATE = #{state}
WHERE STORE_ID = #{storeId}

UPDATE STORE
SET STORE_NAME = #{storeName}, STORE_ADDR = #{storeAddr}, STORE_PHONE = #{storePhone}
WHERE STORE_ID = #{storeId}

mapper.select:
SELECT STORE_ID, STORE_PW
FROM STORE
WHERE STORE_ID = #{storeId}
AND STORE_PW =
#{password}
